import React from 'react';

class CheckboxComponent extends React.Component{
    constructor(props){
        super(props);
    }
    handleChange=(event)=>{
        this.props.inputCheckChange(event.target.value);
    }
    render(){
    return(
        <div>
            <p>
            <input type="checkBox" checked={this.props.inputCheck} onChange={this.handleChange}/>
            Only show products in stock
            </p>
        </div>
    );
    }
}
export default CheckboxComponent;
