import { thisExpression } from '@babel/types';
import ProductCategory from './ProductCategory';
import ProductRow from './ProductRow';

class ProductDisplay extends React.Component{
    constructor(props)
    {
        super(props);
    }
    displayProductList=()=>{
        let rows=[];
        const productsLength=this.props.products.length;
        let changeCategory=null;
        const text=this.props.inputText;
        const inStock=this.props.inputCheck;
        for(var i=0;i<productsLength;i++)
        {
            const productEachElement=this.props.products[i];
            console.log(text,productEachElement.name.indexOf(text));
            if(productEachElement.name.indexOf(text) === -1) {
                return;
              }
            if(productEachElement.name.indexOf(text)!==-1){
                if(productEachElement.category!==changeCategory)
                    {
                rows.push(
                    <ProductCategory category={productEachElement.category}/>
                );
                }
                rows.push(
                    <ProductRow product={productEachElement}/>
                );
                return rows
            }
            if(productEachElement.category!==changeCategory)
            {
                rows.push(
                    <ProductCategory category={productEachElement.category}/>
                );
            }
            rows.push(
                <ProductRow product={productEachElement}/>
            );
            changeCategory=productEachElement.category;
        }
        return rows
    }
    render(){
        return(
            <div>
                <table>
                    <tr>
                        <th>Name</th><th>Price</th>
                    </tr>
                </table>
                <tbody>
                {this.displayProductList()}
                </tbody>
            </div>
        );
    }
}
export default ProductDisplay ;
