import React from 'react';
import SearchComponent from './SearchComponent';

class ProductListFilters extends React.Component{
    constructor(props){
        super(props);
        this.state={
            text:'',stockStatus:false
        }
    }
    handleTextChange=(text)=>{
        this.setState({text:text});
    }
    handleStockChange=(stockStatus)=>{
        this.setState({stockStatus:stockStatus});
    }
    render(){
    return(
        <div>
            <SearchComponent inputText={this.props.text} inputTextChange={this.handleTextChange}/>
            <checkboxComponent inputCheck={stockStatus} inputCheckChange={this.handleStockChange}/>
         </div>
    );
    }
}
export default ProductListFilters;
